sp_header_footer.client_script:
function($scope, $window) {
  var c = this;

  // Initialize variables
  c.searchTerm = '';

  // Function to search courses
  c.searchCourses = function() {
    if (!c.searchTerm.trim()) return;

    // Navigate to course page with search term
    $window.location.href = '?id=cem_courses&search=' + encodeURIComponent(c.searchTerm);
  };
}

