sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	const MAX_STUDENTS = 20;
	var uniqueStudents = new Set();

	var gr = new GlideRecord('x_1620151_class_en_enrollments');
	gr.addQuery('section', current.section);

	// exclude current record if updating
	if (current.operation() === 'update') {
		gr.addQuery('sys_id', '!=', current.sys_id);
	}
	gr.query();

	while (gr.next()) {
		if (gr.student) {
			uniqueStudents.add(gr.student.toString());
		}
	}

	// add the current student being enrolled
	uniqueStudents.add(current.student.toString());

	var totalStudents = uniqueStudents.size;
	if (totalStudents > MAX_STUDENTS) {
		gs.addErrorMessage('Maximum is ' + MAX_STUDENTS + '. Cannot enroll another student.');
		current.setAbortAction(true);
	} else {
		var remaining = MAX_STUDENTS - totalStudents;
		if (remaining > 0) {
			gs.addInfoMessage('That section currently has ' + totalStudents + ' student(s). You can still add ' + remaining + ' more student(s).');
		} else {
			gs.addInfoMessage('That was the last slot. Section is now full.');
		}
	}

})(current, previous);
