// Validate if student already has this course
var dupEnroll = new GlideRecord("x_1620151_class_en_enrollments");
dupEnroll.addQuery("student", current.student);
dupEnroll.addQuery("course", current.course);
dupEnroll.addQuery("state", "enrolled");
dupEnroll.query();

if (dupEnroll.hasNext()) {
    workflow.scratchpad.validationError = "Student already enrolled in this course";
    workflow.scratchpad.validation = false;
    return;
}

// Validate class capacity
var enrollCount = new GlideAggregate("x_1620151_class_en_enrollments");
enrollCount.addQuery("section", current.section);
enrollCount.addQuery("state", "enrolled");
enrollCount.addAggregate("COUNT");
enrollCount.query();

if (enrollCount.next() && parseInt(enrollCount.getAggregate("COUNT")) >= 20) {
    workflow.scratchpad.validationError = "Class has reached maximum capacity of 20 students";
    workflow.scratchpad.validation = false;
    return;
}

// Validate unit limit
var totalUnits = 0;
var newUnits = 0;

var courseGr = new GlideRecord("x_1620151_class_en_courses");
if (courseGr.get(current.course)) {
    newUnits = parseInt(courseGr.getValue("units")) || 0;
    
    // Get student's current approved enrollments
    var enrollGr = new GlideRecord("x_1620151_class_en_enrollments");
    enrollGr.addQuery("student", current.student);
    enrollGr.addQuery("state", "enrolled");
    enrollGr.query();
    
    while (enrollGr.next()) {
        var enrolledCourse = new GlideRecord("x_1620151_class_en_courses");
        if (enrolledCourse.get(enrollGr.getValue("course"))) {
            totalUnits += parseInt(enrolledCourse.getValue("units")) || 0;
        }
    }
    
    if (totalUnits + newUnits > 21) {
        workflow.scratchpad.validationError = "Exceeds student's unit limit of 21. Current units: " + totalUnits + ", New units: " + newUnits;
        workflow.scratchpad.validation = false;
        return;
    }
}

// All validations passed
workflow.scratchpad.validation = true;

